INSERT INTO `posts` (`id`, `title`, `content`, `created_at`, `updated_at`, `events_id`, `deleted_at`, `views`) VALUES
(1, 'خالق انیمیشن «شیرشاه» بر اثر کرونا درگذشت', 'آن سالیوان انیماتور فیلم انیمیشن سینمایی معروف «شیرشاه» سومین عضو خانه بازنشستگی تلویزیون وموشن پیکچرز است که بر اثر کرونا جان خود را ازدست می دهد.\r\n\r\nآن سالیوان انیماتور «شیرشاه» و «پری کوچک دریایی» محصول دیزنی، در ۹۱ سالگی بر اثر ابتلا به بیماری کووید ۱۹ درگذشت.\r\n\r\nانجمن بازنشستگان وودلند هیلز متعلق به صندوق تلویزیون و موشن پیکچرز خبر درگذشت سالیوان بر اثر مشکلات ناشی از بیماری کووید ۱۹ را رسما اعلام کرد.\r\n\r\n«اولیور و رفقا»(۱۹۸۸) «پری کوچک دریایی» (۱۹۸۹) «دانگرفیلد خانه به دوش»(۱۹۹۱) «دنیای خوب»(۱۹۹۲) «شیرشاه»(۱۹۹۴) «هرکولها»(۱۹۹۷) «تارزان»(۱۹۹۹) «زندگی جدید امپراتور»(۲۰۰۰) «لیلو و استیچ» و «سیاره گنج»(۲۰۰۲) ازجمله آثار شاخص او در زمینه فیلم های سینمایی انیمیشن هستند.', '2020-04-16 11:31:57', '2020-05-07 18:31:27', NULL, NULL, 67),
(2, 'پوستر رسمی«خوب بد جلف۲؛ارتش سری» منتشر شد/ برگزاری اولین اکران مردمی', 'پوستر فیلم سینمایی «خوب بد جلف۲؛ ارتش سری» به کارگردانی پیمان قاسم خانی و تهیه‌کنندگی محسن چگینی با طراحی شـایان شـفابخـش منتشر شد.\r\n\r\nاولین اکران مردمی «خوب بد جلف۲؛ ارتـش سـری» با حضور سام درخشانی، پژمان جمشیدی، حامد کمیلی و ریحانه پارسا چهارشنبه ۳۰ بهمن از ساعت ۱۹ الی ۲۲ در پردیس سینما کورش برگزار می شود.', '2020-04-16 10:34:39', '2020-04-30 19:32:01', 2, NULL, 41),
(3, '«مطرب» پرفروش‌ترین فیلم تاریخ سینمای ایران شد/ «هزارپا» پرمخاطب‌ترین فیلم ماند', 'در حالی که سال گذشته بسیاری فروش ۳۸ میلیاردی «هزارپا» را دست نیافتنی می‌دانستند، فیلم «مطرب» در پانزدهمین هفته اکرانش توانست گوی سبقت را از «هزارپا» برباید و عنوان پرفروش‌ترین فیلم تاریخ سینمای ایران را از آن خود کند.\r\n\r\nبا آغاز جشنواره فجر کمتر کسی تصور می‌کرد فروش بالای «مطرب» ادامه پیدا کند اما فیلم مصطفی کیایی فاصله بیش از یک میلیاردی اش با «هزارپا» را پشت سر گذاشت و حالا عنوان پرفروش‌ترین فیلم سینمای ایران را در اختیار دارد\r\n\r\nبا این وجود تعداد مخاطبان فیلم «مطرب» تاکنون بیش از 3،200،000 هزار نفربوده است که با تعداد مخاطبان 4 میلیونی هزارپا فاصله قابل توجهی دارد. بعید است «مطرب» حتی با ادامه اکران هم بتواند از نظر تعداد مخاطب به رکورد هزارپا دست یابد.', '2020-04-16 09:48:05', '2020-05-06 18:41:00', 1, NULL, 14),
(4, '«خوب، بد، جلف ۲» اولین گزینه اکران نوروز ۹۹ شد/ «عطر داغ» از ۳۰ بهمن در سینما', 'به گزارش سینماتیکت غلامرضا فرجی سخنگوی شورای صنفی نمایش درباره آخرین مصوبات جلسه امروز یکشنبه ۲۷ بهمن ماه گفت: فیلم سینمایی «خوب، بد، جلف ۲»  به کارگردانی پیمان قاسمخانی و تهیه کنندگی محسن چگینی از روز چهارشنبه سی بهمن ماه به سر گروهی سینما آزادی به عنوان اولین گزینه نوروزی سال ۹۹ اکران می‌شود و فیلم «عطر داغ» اولین ساخته سینمایی علی ابراهیمی و فیلم «چهل و هفت نفر» به سر گروهی باغ کتاب از روز چهارشنبه ۳۰ بهمن ماه اکران خود را آغاز می‌کنند. \r\n\r\nوی در پایان گفت: کف فروش بقیه فیلم ها به گونه ای است که اکران آنها ادامه می یابد.\r\n\r\n', '2020-04-16 08:50:20', '2020-05-07 18:26:37', 2, NULL, 14),
(5, 'شرکت بانو فیلم با «خوب، بد، جلف ۲: ارتش سری» شروع به کار کرد', 'شرکت «بانو فیلم» به مدیریت تینا پاکروان به تازگی فعالیت سینمایی خود را آغاز کرده است. این شرکت پخش فیلم کمدی «خوب بد جلف 2؛ ارتش سری» به کارگردانی پیمان قاسمخانی را بر عهده دارد.\r\nتینا پاکروان مدیرمسئول «بانو فیلم» متولد سال 1356 تهران است. دوره لیسانس کارگردانی را در دانشکده‌ی سینما-تئاتر تهران و تحصیلات تکمیلی‌اش را در دانشگاه یوسی‌ال آمریکا گذرانده است. او به جز کارگردانی و بازیگری تجربه فعالیت در زمینه‌های مختلف سینمایی را دارد و همچنین با بزرگانی نظیر بهرام بیضایی، مسعود کیمیایی، محمد ورزی، ضیالدین دری و ... کار کرده است.\r\nبعد از انجام فعالیت‌های گوناگون و ساختن پنج فیلم کوتاه و مستند او سرانجام 20 سال پس از شروع اولین فعالیت‌های سینمایی‌اش، در سن ۳۶ سالگی، اولین فیلم بلند سینمای‌اش «خانوم» را می‌سازد. او همچنین نویسنده، تهیه‌کننده و سرمایه‌گذار این فیلم هم هست. «خانوم» با بازی امین حیایی، شقایق فراهانی، سیامک انصاری، اشکان خطیبی و ... در سی‌ونهمین جشنواره بین‌المللی فجر-1392 به نمایش گذاشته شد. پاکروان هم‌زمان به‌عنوان بازیگر فیلم «مترو پل» مسعود کیمیایی نیز در این جشنواره حضور دارد. «زنان امروزی» و ایستادگی آنان در مقابل مشکلات، مهم‌ترین دغدغه او در اولین ساخته بلند سینمایی‌اش است. پاکروان در سال 94 باز هم به هیبت کارگردان با فیلم «نیمه‌شب اتفاق افتاد» در جشنواره فجر حضور پیدا می‌کند. \r\nاز فعالیت‌های دیگر او می‌توان به تدریس، انتشار کتاب، نقد، ترجمه، برگزاری نمایشگاه عکس اشاره‌کرد.', '2020-04-16 07:50:20', '2020-04-16 05:23:01', 2, NULL, 9),
(6, 'سروش صحت: نمی‌خواهم به مرگ فکر کنم ولی نمی‌شود', 'بخش‌هایی از گفتگو با سروش صحت درباره فیلم «جهان با من برقص» را بخوانید.\r\n\r\nبا توجه به سابقه‌ای که در نویسندگی و بازیگری دارید، چه اتفاقی افتاد که تصمیم گرفتید فیلم بسازید؟ کارگردانی سینما را اساسا چرا این همه دیر شروع کردید؟\r\n\r\nبله، من دیر وارد عرصه سینما و تلویزیون شدم، یعنی وقتی برای اولین‌بار به عنوان بازیگر و نویسنده در سینما و تلویزیون مشغول شدم، سنم زیاد بود و هیچ شناختی از این مقوله نیز نداشتم. باید سال‌ها می‌گذشت تا بدانم قضیه از چه قرار است. بعد از مدتی کارگردانی تلویزیون به من پیشنهاد شد که از آن هم هیچ شناختی نداشتم. باید چند سریال می‌ساختم تا اعتماد به نفس لازم برای کار کردن در سینما را به دست بیاورم. تا قبل از فیلم «جهان با من برقص» از نظر روحی، روانی و ذهنی آمادگی ساخت یک کار سینمایی را نداشتم و احساس می‌کردم در این زمینه تجربه لازم را ندارم.\r\n\r\nدر سال‌های اخیر کمدی‌های خوبی ندیدیم. با توجه یه اینکه جهان با من برقص اولین فیلم سینمایی شماست و رگه‌های کمدی هم در آن لحاظ شده، نگران چگونگی قضاوت شدن نبودید؟\r\n\r\nاتفاقا نگرانی من برعکس بود، چون من این کار را طنز نمی‌دانم و معتقدم فیلم «جهان با من برقص» فیلم طنز نیست و از این بابت نگران بودم که نکند مخاطب انتظار کار کمدی از این فیلم داشته باشد و بعد که این فیلم را ببیند دلخور شود که چرا خنده‌دار نبود. البته خوشحالم الان که مخاطب به سینما می‌رود به قسمت‌هایی از فیلم می‌خندد چون فکر می‌کردم ممکن است مخاطب با تماشای این صحنه‌ها فقط لبخند بزند، اما خدا را شکر خنده مخاطب در طول فیلم بیشتر از انتظار من است.\r\n\r\nبعضی فیلمسازها مرگ‌اندیش هستند. شما هم همین‌ طورید؟\r\n\r\nبله، خیلی زیاد به مرگ فکر می‌کنم. دوست دارم به آن فکر نکنم ولی نمی‌شود .\r\n\r\nیادآوری مرگ به خودی خود ترسناک است ولی فیلم «جهان با من برقص» ترس را به مخاطب القا نمی‌کند.\r\n\r\nراستش را بخواهید به نظر من هم مرگ بسیار ترسناک است، اما معتقدم بزرگ‌ترین اتفاق زندگی ما آدم‌ها مرگ است. به عبارتی دو اتفاق مهم در زندگی همه ما روی می‌دهد که مهم است؛ برخلاف عده‌ای که ازدواج کردن و بچه‌دار شدن را مهم می‌دانند، نظر من این است که تولد و مرگ از هر چیزی در این دنیا مهم‌ترند. به گمان من این برهه از زمان و زندگی - البته فارغ از حیات اخروی - وقتی تمام می‌شود ناراحت‌کننده است. هر پایانی آدم را ناراحت می‌کند. ما وقتی دوره دبستان را هم تمام می‌کنیم و به راهنمایی می‌رویم، ناراحت می‌شویم چون یک‌سری از دوستان‌مان را از دست می‌دهیم و شاید اصلا ترک تحصیل کنیم. راستش دلم می‌خواهد راه‌ها و روش‌هایی برای ذهنم در رویارویی با مرگ پیدا کنم که این غم و سیاهی بزرگ را که ناشناخته و عظیم و عجیب و گنگ و مبهم است، راحت‌تر بپذیرم به خصوص اینکه اکنون نه تنها وارد میانسالی شدم که میانسالی را هم تقریبا دارم پشت سر می‌گذارم. در «جهان با من برقص» تلاش کردم، کاری کنم که تمام این دغدغه‌ها و فکرها برای خودم و مخاطبم دوست‌داشتنی‌تر و راحت‌تر شوند چون مرگ ناشناخته بزرگی است.', '2020-04-16 02:00:40', '2020-04-23 19:34:00', 21, NULL, 8),
(7, '«جهان با من برقص» به صدر جدول رسید/ جدول فروش تا 29 بهمن', 'فیلم «جهان با من برقص» به کارگردانی سروش صحت در هفتمین هفته اکرانش با فروش 663 میلیون تومان توانست از «مطرب» عبور کرده و خود را به صدر جدول برساند. \r\n\r\nدر خلاصه داستان فیلم آمده است: دوستان جهانگیز برای آخرین تولد او دورهم جمع شده‌اند. این موقعیت موجب می‌شود آن‌ها درباره زندگی و خودشان فکر کنند.\r\nعلی مصفا، جواد عزتی، هانیه توسلی، پژمان جمشیدی، سیاوش چراغی‌پور، کاظم سیاحی و مهراب قاسم‌خانی در این فیلم به ایفای نقش پرداخته‌اند.\r\n\r\n«مطرب» به کارگردانی مصطفی کیایی با فروش هفتگی 586 میلیون تومان در رتبه دوم جدول قرار گرفت. همچنین «مطرب» این هفته به مجموع فروش 38.2 دست یافت و عنوان پرفروش‌ترین فیلم تاریخ سینما را از آن خود کرد لقبی که پیش از این در اختیار هزارپا بود. با این وجود تعداد مخاطبان «مطرب» تا کنون بیش از 3 میلیون نفر بوده است که بعید به نظر می‌رسد در مدت باقی مانده به رکورد 4 میلیون مخاطب هزارپا دست پیدا کند.\r\n\r\n«زیر نظر» با فروش 453 و «چشم و گوش بسته» با فروش 341 دیگر کمدی‌های پرفروش هفته بودند که به ترتیب در رتبه سوم و چهارم جدول فروش قرار گرفتند.\r\n\r\nپنجمین فیلم پرفروش هفته نیز «تورنادو» ساخته جواد هاشمی با فروش 262 میلیون بود. این فیلم که مخصوص کودکان و نوجوانان است، داستان پروفسوری را روایت می‌کند که موفق به ساخت یک رستوران شده است. رستورانی که در نهایت به یک سفینه تبدیل می‌شود. حال با ورود چهار نوجوان به قصه و با شیطنت آنان، این سفینه مقداری زودتر از موعد به پرواز درمی‌آید. سفینه در نهایت به فضا رفته و در یک سرزمین ناشناخته فرود می آید.\r\n', '2020-04-16 02:10:38', '2020-04-17 07:40:03', 21, NULL, 11),
(9, 'حال این روزهای تماشای آنلاین خوب است', 'علیرضا تابش، مدیرعامل بنیاد سینمایی فارابی\r\nتلاطم زیادی در خصوص وضعیت کسب و کارها وجود دارد، و ویدیوهای مختلفی دائما منتشر می‌شود تا مردم جویای اخبار را به ماندن در خانه تشویق کند اما در این میان، به نظر می‌رسد کسب و کارهای آنلاین از جمله بخش‌هایی هستند که روزهای خوبی را پشت سر می‌گذارند. این موضوع نه فقط به خریدهای آنلاین، که ارائه‌دهندگان سرویس‌ ویدیوهای درخواستی را نیز شامل می‌شود. شاید این موضوع علی‌رغم تعطیلی سالن‌های سینما، برای فعالان حوزه فیلم که اغلب روش‌های سنتی نمایش فیلم، یکی از گره‌های کاری آن‌ها به‌ویژه برای عرضه بین‌المللی بوده، زیاد هم خبر بدی نباشد.\r\n\r\nاز آنجا که در روزهای آغازین سال نو و با استفاده از توفیق اجباری قرنطینه‏، در حال کار بر روی پایان‏نامه‏ام با موضوع «بازاریابی دیجیتال برای صنعت سینمای کشور» بودم، شرایط و احوال این روزها، روایت دیگری در ذهن من شکل داده است، حقیقت این است که علاوه بر بازار تغییرشکل یافته و منحصر به فرد سینما در ایران باعث شده است، فرآیند بازاریابی بین‏المللی سینما در کشورمان، عموماً در محدوده‏ی تلاشهای تجربی و سنتی باقی بماند، حالا مواجهه‏ی این هنر/صنعت با این شرایط جدید و خیل مشتاقان و کاربران داخلی و خارجی، و گسترش قابل توجه ترافیک اینترنت در کشور، فعالان این حوزه را با شرایط جدیدی مواجه کردند.\r\nالبته پخش‌کنندگان بین‌المللی ایران به تجربه دریافته‌اند که کانال توزیع باید با فرهنگ مصرف در بازار هر کشور متناسب باشد و اکران سینمایی لزوماً راه مناسبی برای بازار فیلم همه کشورها و سهم گرفتن از مخاطب فیلم‌بین آن‌ها نیست. به‌کارگیری فناوری در پخش و عرضه آنلاین و روی آوردن فعالان جوان به این روش‌ها، نقشه راه جدیدی را وارد بحث توزیع بین‌المللی آثار تصویری ایران کرده است.\r\nدر این نقشه راه جدید، لازم است که ویژگیهای پخش آنلاین و اکران اینترنتی به خوبی مورد مداقه و بررسی قرار گیرد. لزوم تناسب رسانه با عادات مخاطبان را نیز نباید از نظر دور داشت. به گفته دیگر، در سیستم پخش بین‌المللی سنتی باید تغییر و به‌روزرسانی صورت گیرد تا هم بتوان به مخاطبان نهایی فیلم‌بین در کشورهای مختلف دسترسی بهتری پیدا کرد و هم مخاطبان جدیدی را از نسل فناوری‌جو جذب نمود. این کار مستلزم تخصصی کردن سیستم توزیع داخلی و بین‌المللی است که برای آن، عرضه باید متناسب با قالب و ژانر فیلم و محصول تصویری و نیز عادات مخاطبان در بازارهای هدف مختلف صورت گیرد. شناخت «لبه‏ی تکنولوژی» و پرهیز از استفاده از فناوریهای قدیمی شده، الزام دیگری است که باید به جد آن را رعایت کرد، اختراع دوباره‏ی چرخ و تشکیک در امور اثبات شده از آسیبهای جدی انتقال تکنولوژی به کشورمان بوده است که باید از آن در این حوزه اجتناب کرد.\r\nمساله‌ی دیگری که نباید از آن غافل شد، این است که فناوری دیجیتال موجب پدید آمدن بازارهای جدیدی برای محصولات تصویری در دنیا شده است. این بازارهای جدید تحت تاثیر فناوری، به دلیل ظهور رسانه‌های جدید و تعریف حق‌امتیازهای جدید برای آن‌ها شکل گرفته‌اند که می‌توان در تدوین نقشه راه اشاره شده برای صنعت تصویر ایران در بخش عرضه بین‌المللی، به آن توجه کرد و از آن سهم گرفت. با افزایش سهم از هریک از بخش‌های بازار، می‌توان میزان درآمد حاصل از هر محصول را نیز افزایش داد. علاوه بر آن،فناوری ، نقشه رقابت در بازارهای فیلم را دگرگون کرده که در صورت نادیده گرفته‌شدن می‌تواند در نهایت به از دست دادن سهم بازارهای موجود نیز بیانجامد. بنابراین، صنعت فیلم و تصویر در ایران باید در مدل‌های کسب و کار خود، نه ‌تنها روش‌های توزیع آنلاین را منظور کند، باید بتواند فراتر از آن به عنوان کشوری صاحب سینما در منطقه، با آینده‌نگری در جهت تبدیل‌شدن به هاب پخش و ارتباطاتی محصولات تصویری و راه‌اندازی سرویس منطقه‌ای ویدیوهای درخواستی گام بردارد.\r\nوضعیت به‌وجودآمده پس از تعطیلی سالن‌های سینما در پی شیوع بیماری همه‌گیر کوئید-۱۹ و البته افزایش دوباره در فعالیت کانال‌های تلگرامی دانلود غیرقانونی فیلم‌ها و محصولات تصویری، نشان از بی‌پاسخ ماندن تقاضای بخش‌هایی از بازار محصولات تصویری از طریق عرضه قانونی به‌ویژه در داخل کشور است. کندی پیگیری این موضوع به بهانه زیرساختی‌ مانند سرعت و کیفیت اینترنت نیز شاید دیگر دلیل خوبی نباشد، چرا که معلوم شد وضعیت کشورهای دیگر دنیا نیز در زمان اوج‌گیری تقاضا بهتر از این نبوده و کمپانی‌هایی مانند نتفلیکس را مجبور ساخته تا با کاهش کیفیت ویدیوهای ارائه‌شده تا ۲۵ درصد از بار به‌ وجود آمده بر روی مصرف اینترنت را در کشورهای مختلف بکاهد. خبرهایی مانند اکران آنلاین فیلم‌هایی مانند «خروج» که قید اکران سینمایی را در شرایط فعلی زده‌اند، اقدام شجاعانه‌ای است که می‌تواند پیشرو تحولی در وضعیت فعلی نمایش آثار سینمایی باشد و البته فرصت بررسی نتایج ناشی از این اقدام را نیز در شرایط ناگزیر فعلی فراهم آورد. البته، «خروج» اولین فیلمی نیست که دست به چنین اقدامی می‌زند، اما اولین محصول در این حد و اندازه است که فرایند عرضه آن با اکران آنلاین آغاز می‌شود. این کار، نه فقط برای بازار داخل، بلکه برای ایرانیان خارج از کشور که در این روزها در شرایط مشابهی به سر می‌برند، لازم بوده تا تهدید ناشی این بیماری به فرصت مناسبی برای ایجاد بازار جدید و گسترش بازارهای قبلی تبدیل شده و بدانند به جای دانلودهای غیرقانونی، می‌توانند به‌روزترین آثار تصویری و سینمایی را با عضویت در VOD‌ها و IPTV‌های رسمی و قانونی به تماشا بنشینند.', '2020-04-23 10:33:28', '2020-05-03 09:21:10', NULL, NULL, 61);

